mapper.update:
update
set DJRQ=now(), FJHM=#{FJHM}, ZXID=#{ZXID}, ZXXM=#{ZXXM}, ZT=#{ZT}, ZXZ=#{ZXZ}, ZXLX=#{ZXLX}, ZXYH=#{ZXYH}, ZXGH=#{ZXGH}, ZXJS=#{ZXJS}, ROLE_ID=#{ROLE_ID}, dept=#{dept}
,PWD = #{PWD}
where ID = #{ID}

mapper.select:
select a.ZXJS,a.DJRQ,a.FJHM,a.ZXID,a.ZXRQ,a.ZXXM,a.ZT,a.ROLE_ID, a.ZXZ,a.ZXLX,a.ZXYH,a.ZXGH,a.PWD, a.TOKENID,a.ID,a.dept, e.NAME as ZXJSNAME,f.zmc,f.zbh,f.zjn,e.BIANMA,b.ROLE_NAME,b.menu_ids, group_concat(d.NAME) as ZXLXNAME from a left join SYS_DICTIONARIES e on a.ZXJS=e.DICTIONARIES_ID left join TZXZ f on a.ZXZ=f.ID left join sys_role_front b on a.role_id=b.role_id left join SYS_DICTIONARIES d on find_in_set (d.DICTIONARIES_ID,a.ZXLX)
where 1=1
and ZXYH= #{ZXYH}
and ZXID= #{ZXID}
and a.ID != #{ID}
and a.PWD = #{PWD}
group by a.ID


select * from TZXLB t 
where 1=1 and ID = #{ID}
and  ZXZ in (
	select  ZXZ from T_CUS_FP where  TASKID = #{TASKID}
)
