mapper.update:
update
set isdel=#{isdel}, remark=#{remark} where id in
#{item}

update
set
tel=#{tel}, subremark=#{subremark}, supremark=#{supremark}, czman=#{czman}, czdate=now(), ip=#{ip}, type=#{type} where id = #{id}

mapper.select:
select a.id, a.tel, a.supremark, a.subremark, a.czman, a.shman, a.shremark, a.ip, a.issh, a.type, a.ucid, DATE_FORMAT(a.shdate,"%Y-%m-%d %T") as shdate, DATE_FORMAT(a.czdate,"%Y-%m-%d %T") as czdate, c.zxxm as czname, d.zxxm as shname, f.file_name, f.start_time, f.end_time, f.ext_no from a left join ccrecord.t_recording_track f on a.ucid=f.ucid left join tzxlb c on a.czman=c.id left join tzxlb d on a.shman=d.id where 1=1
and ( a.tel LIKE CONCAT(CONCAT('%', #{pd.tel}),'%') )
and ( c.zxid LIKE CONCAT(CONCAT('%', #{pd.czman}),'%') or c.zxxm LIKE CONCAT(CONCAT('%', #{pd.czman}),'%') )
and a.name LIKE CONCAT(CONCAT('%', #{pd.name}),'%')
and a.czdate >= #{pd.starttime}
and a.czdate <= #{pd.endtime}
and a.subremark LIKE CONCAT(CONCAT('%', #{pd.subremark}),'%')
and a.departmentid=#{pd.departmentid}
and a.issh=#{pd.issh}
and c.zxid=#{pd.zxid}
and c.dept=#{pd.dept}
and a.isdel=#{pd.isdel}
order by a.czdate desc
