mapper.select:
select a.id, a.type, a.phone, a.name, a.receivetime, a.content, a.customid, a.ucid, a.createman, a.createdate, c.zxxm as createmanName, b.NAME as typename, d.name as customName, d.recepdep, d.position, d.sex, d.idcard, a.cate, c.zxid from a left join SYS_DICTIONARIES b on a.type=b.DICTIONARIES_ID left join tzxlb c on a.createman=c.id left join t_vipinfo d on a.customid=d.id where 1=1
and ( a.name LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or a.content LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or a.phone LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and a.phone LIKE CONCAT(CONCAT('%', #{pd.phone}),'%')
and a.receivetime >= #{pd.starttime}
and a.receivetime <= #{pd.endtime}
and a.createman=#{pd.createman}
and a.createman=#{pd.userid}
and c.zxid=#{pd.zxid}
and c.ZXZ=#{pd.ZXZ}
and c.dept=#{pd.dept}
order by a.createdate desc
