mapper.insert:


INSERT INTO ${tableName}(${lineColumn}) 
select result.* from( 

 select  
 ${lineValues}
	from dual) 



 result

mapper.update:
update
	${TABLENAME} 
set 
FPMAN=#{FPMAN},	
ZXMAN=#{ZXMAN}
where 
	ID = #{ID}

update
	${TABLENAME} 
set 
HFWJ=#{HFWJ},	
HFDATE=now(),	
HFRESULT=#{HFRESULT},
HFREMARK=#{HFREMARK}
where 
	ID = #{ID}

mapper.select:
select ${FIELD},b.ZXXM from ${TABLENAME} a left join TZXLB b on a.ZXMAN=b.ID ,(SELECT @i:=0) t where 1=1
and ZXMAN = #{ZXMAN}
order by a.ID

select ${FIELD},b.ZXXM from ${TABLENAME} a left join TZXLB b on a.ZXMAN=b.ID where 1=1
and ZXMAN = #{ZXMAN}
and hfwj is null order by ROWNO

select ${FIELD},b.ZXXM from ${TABLENAME} a left join TZXLB b on a.ZXMAN=b.ID where 1=1
and ZXMAN = #{ZXMAN}
and hfwj='1' and ZXMAN is not null order by ROWNO


SELECT ${FIELD} FROM (SELECT ${FIELD}  FROM ${TABLENAME} ORDER BY ID) t WHERE 1=1 
and ZXMAN is null

	and  ROWNUM <= #{ROWNUM}

select a.*,b.ZXXM,b.ZT from ${TABLENAME} a left join TZXLB b on a.ZXMAN=b.ID where 1=1
and a.ZXMAN = #{ZXMAN}
and a.ZXMAN in (${ZXMANSTR})
