mapper.update:
update

set 
STATE=#{STATE},
TASKMAN=#{TASKMAN}
where 
	ID = #{ID}

mapper.select:
select 

from 

where 
	CODE = #{CODE}

		and ID != #{ID}

select a.ID, a.NAIRE_TEMPLATE_ID, a.CUSTOM_TEMPLATE_ID, a.TYPE, c.NAME as TYPENAME, a.TASKNAME, COMPLETEDATE,
a.STATE, a.TASKMAN, a.CREATEMAN, a.CREATEDATE, b.NAME as CUSTOM_TEMPLATE_NAME, d.NAME as NAIRE_TEMPLATE_NAME, a.TABLENAME, a.FPTYPE, a.ZXIDSTR from a left join T_CUSTOM_TEMPLATE b on b.ID=a.CUSTOM_TEMPLATE_ID left join T_NAIRE_TEMPLATE d on d.ID=a.NAIRE_TEMPLATE_ID left join SYS_DICTIONARIES c on a.TYPE=c.DICTIONARIES_ID where 1=1
and ( TASKNAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and STATE= #{pd.STATE}
and COMPLETEDATE+1>#{pd.COMPLETEDATE}

select

from 

where tablename is null and ID= #{ID}

select
${FIELD},count(*) as counter
from 
${TABLENAME} 
group by ${FIELD}
