mapper.select:
SELECT * FROM t_vipinfo
,(SELECT vipid FROM t_ortherlink WHERE otel=#{pd.tel}) a where id=a.vipid
WHERE 1=1
and id=#{pd.id}
and name=#{pd.name}
and result=#{pd.result}
AND (name LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') OR result LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') OR idcard LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') OR sex LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') OR isimport LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') OR place LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') OR clevel LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') OR ctype LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%'))
order by cztime desc

mapper.update:
UPDATE t_vipinfo
SET
result=#{result}
WHERE id=#{id}

UPDATE t_ortherlink
SET
oname=#{oname},
otel=#{otel},
vipid=#{vipid}
WHERE id=#{id}

UPDATE t_ortherlink
SET
vipid=#{vipid}
WHERE id=#{id}

UPDATE t_photo
SET
vipid=#{vipid}
WHERE id=#{id}
